package PrimitiveTypesInfo;

public class PrimitiveTypesInfo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		float aPrimitive = 32.2f;
		System.out.println("Value:               "+aPrimitive);
		System.out.println("Type:                "+Float.TYPE);
		System.out.println("Size in memeory:     "+Float.SIZE + "bits.");
		System.out.println("Minimum Value:       "+Float.MIN_VALUE);
		System.out.println("Maximum Value:       "+Float.MAX_VALUE);
	}

}
